INSERT INTO localstrings (keycode, langcode, stringvalue) VALUES ('auth_SPID_BUSINESS_LOGIN_DESCRIPTION', 'it', 'SPID per uso professionale è lo SPID "tipo 3" riservato ai liberi professionisti o lo SPID "tipo 4" rilasciato alla persona fisica che opera per conto di una persona giuridica (lidentità` digitale contiene gli attributi della persona giuridica e della persona fisica cui sono state rilasciate le credenziali di autenticazione).');
INSERT INTO localstrings (keycode, langcode, stringvalue) VALUES ('auth_SPID_BUSINESS_LOGIN_DESCRIPTION', 'en', 'SPID for professional use is the "type 3" SPID reserved for freelancers or the "type 4" SPID issued to the natural person who works on behalf of a legal person (the digital identity contains the attributes of the legal person and the natural person to whom authentication credentials have been issued).');
